import { createReducer, on } from '@ngrx/store';
import { startLoading, stopLoading } from './loading.actions';

export interface LoadingState {
  loading: boolean;
}

export const initialLoadingState: LoadingState = {
  loading: false
};

export const loadingReducer = createReducer(
  initialLoadingState,
  on(startLoading, () => ({ loading: true })),
  on(stopLoading,  () => ({ loading: false }))
);

export interface TableColumn {
    key: string;
    label: string;
  }

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { delay } from 'rxjs/operators';

export interface User {
  id: number;
  name: string;
  username: string;
  email: string;

  address: {
    street: string;
    suite: string;
    city: string;
    zipcode: string;
    geo: {
      lat: string;
      lng: string;
    };
  };

  phone: string;
  website: string;

  company: {
    name: string;
    catchPhrase: string;
    bs: string;
  };
}


@Injectable({
  providedIn: 'root',
})
export class UsersService {
  private baseUrl = 'https://jsonplaceholder.typicode.com/users';
  
  constructor(private http: HttpClient) {}

  getAllUsers(): Observable<User[]> {
    return this.http.get<User[]>(this.baseUrl).pipe(
      delay(500) // Delay de 5 segundos para visualiza√ß√£o do loading
    );
  }
}

import type { Meta, StoryObj } from '@storybook/angular';
import { of } from 'rxjs';
import { Table } from '../app/shared/table/table';

interface UserMock {
  id: number;
  name: string;
  email: string;
  company: {
    name: string;
  };
}

const mockUsers: UserMock[] = [
  {
    id: 1,
    name: 'Leanne Graham',
    email: 'leanne@email.com',
    company: { name: 'Romaguera-Crona' },
  },
  {
    id: 2,
    name: 'Ervin Howell',
    email: 'ervin@email.com',
    company: { name: 'Deckow-Crist' },
  },
];

const columns = [
  { key: 'name', label: 'Nome' },
  { key: 'email', label: 'E-mail' },
  { key: 'company.name', label: 'Empresa' },
  { key: 'actions', label: 'A√ß√µes' },
];

const meta: Meta<Table<UserMock>> = {
  title: 'Shared/Table',
  component: Table,
};

export default meta;
type Story = StoryObj<Table<UserMock>>;

export const Default: Story = {
  args: {
    dataColumn: columns,
    dataTable$: of(mockUsers),
    loading: false,
  },
};

export const Loading: Story = {
  args: {
    dataColumn: columns,
    dataTable$: of([]),
    loading: true,
  },
};

export const EmptyState: Story = {
  args: {
    dataColumn: columns,
    dataTable$: of([]),
    loading: false,
  },
};

export const RowClick: Story = {
  args: {
    dataColumn: columns,
    dataTable$: of(mockUsers),
    loading: false,
  },
  play: async ({ canvasElement }) => {
    const row = canvasElement.querySelector('tbody tr');
    row?.dispatchEvent(new MouseEvent('click', { bubbles: true }));
  },
};

import { createReducer, on } from '@ngrx/store';
import { loadUsers, loadUsersSuccess, loadUsersFailure } from './users.actions';
import { User } from '../users.service';

export interface UsersState {
  users: User[];
  error: unknown | null;
}

const savedUsers = localStorage.getItem('users');

export const initialState: UsersState = {
  users: savedUsers ? JSON.parse(savedUsers) : [],
  error: null
};

export const usersReducer = createReducer(
  initialState,
  on(loadUsers, state => ({ ...state, error: null })),
  on(loadUsersSuccess, (state, { users }) => ({ ...state, users })),
  on(loadUsersFailure, (state, { error }) => ({ ...state, error }))
);

